<?if(!defined("B_PROLOG_INCLUDED") || B_PROLOG_INCLUDED!==true) die();?><div id="<? echo $arItemIDs['BASKET_PROP_DIV']; ?>"><?		if (!empty($arResult['PRODUCT_PROPERTIES_FILL']))		{			foreach ($arResult['PRODUCT_PROPERTIES_FILL'] as $propID => $propInfo)			{?>	<input type="hidden" name="<? echo $arParams['PRODUCT_PROPS_VARIABLE']; ?>[<? echo $propID; ?>]" value="<? echo htmlspecialcharsbx($propInfo['ID']); ?>"><?				if (isset($arResult['PRODUCT_PROPERTIES'][$propID]))					unset($arResult['PRODUCT_PROPERTIES'][$propID]);			}		}		$emptyProductProperties = empty($arResult['PRODUCT_PROPERTIES']);		if (!$emptyProductProperties)		{			foreach ($arResult['PRODUCT_PROPERTIES'] as $propID => $propInfo)			{				if(					'L' == $arResult['PROPERTIES'][$propID]['PROPERTY_TYPE']					&& 'C' == $arResult['PROPERTIES'][$propID]['LIST_TYPE']				)				{?>	<div class="select-wrap">		<div class="text"><?=$arResult['PROPERTIES'][$propID]['NAME']?>:</div><?					foreach($propInfo['VALUES'] as $valueID => $value)					{						?>		<label class="radio-styled">			<input type="radio" name="<? echo $arParams['PRODUCT_PROPS_VARIABLE']; ?>[<? echo $propID; ?>]" value="<? echo $valueID; ?>" <? echo ($valueID == $propInfo['SELECTED'] ? 'checked="checked"' : ''); ?>>			<span class="radio-content">				<span class="radio-fake"></span>				<span class="text"><? echo $value; ?></span>			</span>		</label><?					}?>	</div><?				}				else				{					?><div class="select-wrap">					<select class="select-styled" name="<? echo $arParams['PRODUCT_PROPS_VARIABLE']; ?>[<? echo $propID; ?>]"><?					foreach($propInfo['VALUES'] as $valueID => $value)					{						?><option value="<? echo $valueID; ?>" <? echo ($valueID == $propInfo['SELECTED'] ? ' selected="selected"' : ''); ?>><?=$arResult['PROPERTIES'][$propID]['NAME']?>: <?=$value?></option><?					}					?></select></div><?				}			}		}?></div>