function initHCarousel(){var $t=$(this),$frame=$t.find(".frame"),$wrap=$frame.closest(".scroll-slider-wrap");if($t.data("sly-inited"))$frame.sly("reload");else{var scroll=$t.find(".sly-scroll"),prev=$t.find(".prev"),next=$t.find(".next");$frame.sly({scrollBar:scroll.get(0),prevPage:prev,nextPage:next}).sly("on","load",function(){$t.data("sly-inited",!0),this.pos.start===this.pos.end?$wrap.addClass("no-scroll"):$wrap.removeClass("no-scroll")}).sly("reload")}}function initHorizontalCarousels(target){var wraps=$(target).find(".scroll-slider-wrap");wraps.filter(":visible").each(initHCarousel),wraps.find("a, button:not(.ctrl-arrow)").on("click",function(e){e.stopPropagation()})}