function initPhotoThumbs(target){$(target).find(".photo-thumbs:visible").each(function(){var _=$(this),dots=_.find(".carousel-dots"),img=_.siblings(".photo").children("a").children("img");_.sly({activateOn:"mouseenter",pagesBar:dots,activatePageOn:"click",pageBuilder:function(index){return'<i class="carousel-dot"></i>'}}).sly("on","active",function(e,i){var src=$(this.items[i].el).children("img").attr("data-medium-image");img.attr("src",src)}).sly("reload")})}